import {View, Text, StyleSheet, Image, ImageBackground, TextInput} from 'react-native';

export default function App() {
  const imgGatin = require('./assets/gatin.jpg')
  const back = { uri: 'https://s2.glbimg.com/sm6kxuuihHobgyVEJ9wAZtRd1WM=/0x0:1280x800/984x0/smart/filters:strip_icc()/i.s3.glbimg.com/v1/AUTH_1f540e0b94d8437dbbc39d567a1dee68/internal_photos/bs/2022/V/Z/7jJ0zHQvqiPIKEQQxPXw/bolo-facil-de-liquidificador.jpg' };
    return(
      <View style = {style.container}>
        <ImageBackground source={back} resizeMode="cover" style={style.image}></ImageBackground>
        <View style = {style.primeira}>
          <Text>Hello, World!!!</Text>
          <TextInput style = {style.input} placeholder="Email" multiline={true} numberOfLines={10}></TextInput>
          <Button title=""/>
        </View>
        <View style = {style.segunda}>
          <Text>Segunda View</Text>
          <Image source={imgGatin} style = {style.imgGatin}/>
          <Image style = {style.imgdrome} source={{uri:'https://i.ytimg.com/vi/9OtuRcD_dBk/maxresdefault.jpg'}}/>
        </View>
      </View>
    );
}

const style = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: 'center',
      justifyContent:'center',
    },
    primeira: {
      flex: 1,
      backgroundColor: '#F1D2E7'
    },
    segunda: {
      flex: 8,
      backgroundColor: '#E2F5E8'
    },
    imgGatin: {
      width: '350px',
      height: '200px',
    },
    imgdrome: {
      width: '350px',
      height: '200px',
    },
    image: {
      flex: 1,
      justifyContent: 'center',
    },
    input: {
      borderBottomColor: 'gray',
      borderBottomWidth: '1px',
      padding: '10px'
    }
});
